import Navsearch from './navsearch.js';
import { useEffect,useState } from 'react';
import { BrowserRouter as Router, Switch ,Link, useHistory, useLocation } from 'react-router-dom';
import useDebounce from '../hooks/useDebounce.js'

import navbarCSS from '../styles/navbar.module.css';

function Navbar( {search, setSearch, coverVal, bookTitle, authorName, onClick,
    setData,
    setBookTitle,
    setCoverVal,
    setAuthorName
} ){

    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const history = useHistory();
    const location = useLocation();

    const newSearch = useDebounce(search.replace(/\s+/g, "+"));

    useEffect(() => {
        const data = async () => {
        const response = await fetch(`https://openlibrary.org/search.json?title=${newSearch}`)
        const data = await response.json();
        setData(data);
        return data;
        }
        data().then(data => {
            data = data.docs;
            // console.log(data)
            setBookTitle(data[0].title);
            setAuthorName(data[0].author_name[0]);
            setCoverVal(`https://covers.openlibrary.org/b/isbn/${data[0].isbn[0]}-M.jpg`);
        });
    },[newSearch])

    useEffect(() =>{
        if(location.pathname === '/'){
            console.log('route changed')
        }
    }, [location])

    const toggleDropdown = (e) => {
        console.log(e.target.textContent)
        if(e.target.textContent === 'signout'){
            history.push('/')
        }

        setIsDropdownOpen(!isDropdownOpen);
    }

    return (
        <Router>
        <nav>
            <div className={navbarCSS.NavLinks}>
                {/*<h1 className={navbarCSS.h1}>BookClub</h1>*/}
                <Link className={navbarCSS.h1} to='/home'><h1>BookClub</h1></Link>

                <Link className={navbarCSS.navlink} to='/books'>books</Link>
                <Link className={navbarCSS.navlink} to='/discussion'>discussion</Link>
                <Navsearch className={navbarCSS.navlink}
                    search={search}
                    setSearch={setSearch}
                    coverVal={coverVal}
                    bookTitle={bookTitle}
                    authorName={authorName}
                    onClick={onClick}
                />
                <div className={navbarCSS.navlink} to='/profile' onClick={toggleDropdown}>
                    personal
                    {isDropdownOpen && (
                      <div className={navbarCSS.dropdownContent}>
                        <div className={navbarCSS.h4}>
                            <Switch>
                            <Link to='/profile' className={navbarCSS.navlink}>
                                <h3>profile</h3>
                            </Link>
                            </Switch>
                        </div>
                        <div className={navbarCSS.h4}>
                            <h3 className={navbarCSS.navlink}>signout</h3>
                        </div>
                    </div>
                    )}
                </div>
            </div>
        </nav>
        </Router>
    );
}

export default Navbar;
