import postCSS from '../styles/post.module.css';
import {useState} from 'react';

const Post = () => {

	const [post, setPost] = useState('');
	const [show, setShow] = useState(false);

	function handleChange(e){
		setPost(e.target.value);
		setShow(false)
	}

	function submit(){
		setShow(true);
		console.log(post);
	}

	return (
		<div className={postCSS.mainDiv}>
			<div className={postCSS.textarea}>
				<textarea placeholder='Add a post:' id={postCSS.textarea} rows='2' cols='10' onChange={handleChange} />
				<button id={postCSS.button} onClick={submit}>post</button>
			</div>

			<div className={postCSS.updates}>
				<p>{show && post}</p>
			</div>
		</div>
	);
}

export default Post;
