import Navbar from "./navbar";
import PersonalUpdate from "./personalUpdate.js";
import Updates from "./updates.js"
import {useState} from 'react';
import PopBookUp from './popBookUp.js'

function App() {

/* Lifted States */

  const [search, setSearch] = useState('');
  const [data, setData] = useState(''); 
  const [bookTitle, setBookTitle] = useState('no title!');
  const [coverVal, setCoverVal] = useState(null);
  const [authorName, setAuthorName] = useState('no author name!');

  const [isOpen, setIsOpen] = useState(false);

  const handleOpenPopup = () => {
    setIsOpen(true);
  }
  const handleClosePopup = () => {
    setIsOpen(false);
  }


  return (
  <>
    <Navbar 
      search={search} 
      setSearch={setSearch}
      coverVal={coverVal}
      bookTitle={bookTitle}
      authorName={authorName}
    />

    <button onClick={handleOpenPopup}>open</button>
    <PopBookUp isOpen={isOpen} >

    </PopBookUp>

    <div className='components'>
      <PersonalUpdate />

      <Updates 
        search={search}
        data={data}
        setData={setData}
        bookTitle={bookTitle}
        setBookTitle={setBookTitle}
        coverVal = {coverVal}
        setCoverVal = {setCoverVal}
        authorName = {authorName}
        setAuthorName = {setAuthorName}
      />

    </div>
  </>

  );
}

export default App;
