import pageCSS from './styles/pages.module.css'
import { useState,useEffect } from 'react'


function Books(){

	const [title, setTitle] = useState('');
	const [author, setAuthor] = useState('');
	const [firstSentence, setFirstSentence] = useState('');
	const [characterList, setCharacterList] = useState([]);

	useEffect(()=> {
		const data = async () => {
			const response = await fetch('https://openlibrary.org/search.json?title=norwegian+wood');
			const data = await response.json();
			return data;
		}
		data().then(responseData => {
			console.log(responseData.docs[0]);
			try{
				setTitle(responseData.docs[0].title);
			}
			catch(error){
				console.log(error);
			}

			try{
				setAuthor(responseData.docs[0].author_name[0]);
			}
			catch(error){
				console.log(error)	
			}

			try{
				setFirstSentence(responseData.docs[0].first_sentence[0])
			}
			catch(error){
				console.log(error)
			}

			try{
				setCharacterList(responseData.docs[0].person)
			}
			catch(error){
				console.log(error)	
			}
		})
	}, [])


	return (
		<div className={pageCSS.booksDiv}>
			<h1>hey books!!!</h1>
			<div className={pageCSS.names}>
				<h3>{title}</h3>
				<h3> - </h3>
				<h3>{author}</h3>
			</div>
			<p><b>The story starts as:</b> <i>{firstSentence === '' ? '':firstSentence}</i></p>
			{characterList === [] ? '' :characterList.map(name => <p>{name}</p>)}
		</div>
	);
}

export default Books;
