import * as React from 'react';
import useEffect from 'react'
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';


import Box from '@mui/material/Box';
import Rating from '@mui/material/Rating';
import Typography from '@mui/material/Typography';

/*  INITIALIZING STATES FOR STATUS AND RAING  */
const [status, setStatus] = React.useState('');
const [value, setValue] = React.useState(0);

/* SELECTING STATUS OF THE BOOK "READING, READ, WANT TO READ" */
export const SelectSmall = function () {


  const handleChange = (event) => {
    setStatus(event.target.value);
    console.log(event.target.value)
  };

  return (
    <FormControl sx={{ m: 1, minWidth: 120 }} size="small">
      <InputLabel id="demo-select-small-label">status</InputLabel>
      <Select
        labelId="demo-select-small-label"
        id="demo-select-small"
        value={status}
        label="status"
        onChange={handleChange}
      >
        <MenuItem value="">
          <em>None</em>
        </MenuItem>
        <MenuItem value={1}>reading</MenuItem>
        <MenuItem value={2}>read</MenuItem>
        <MenuItem value={3}>want to read</MenuItem>
      </Select>
    </FormControl>
  );
}



/* RATING SECTION*/
export const BasicRating = function () {

  return (
    <Box
      sx={{
        '& > legend': { mt: 2 },
      }}
    >
      <Typography component="legend">Rating</Typography>
      <Rating
        name="simple-controlled"
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
          console.log(newValue)
        }}
      />
    </Box>
  );
}


/*  POST REQUEST FOR SENDING STATUS AND RATING DATA   */
export async function submit(){

    const response = fetch('http://localhost:5000/home',{
      method:'POST',
      headers:{
        'Content-Type':'application/json'
      },
      body:JSON.stringify({
        status,
        value
      })
    })
    const data = await response.json();
    console.log(data)
  }
